import React from "react";

class Count extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      count: 0
    };
  }

  // 渲染函数，this 指向实例本身
  render() {
    return (
      <p>
        <button onClick={this.clickCount}>点击我增加一次点击计数</button>
        <p>你已经点击了{this.state.count}次</p>
      </p>
    );
  }

  clickCount = () => {
    this.setState({
      count: this.state.count + 1
    });
  };
}
export default Count;
